import React, { useState } from 'react';
import './CompanySignup.css';
import {Form} from 'react-bootstrap';
import {Button} from 'react-bootstrap';

const Set = () => {
  const [userSignUp, setUserSignUp] = useState({
    i_name: "",
    i_email: "",
    i_password : "",
    i_confirmPass: ""

  });

  let name,value;
  const handleInput = (e) => {
    name = e.target.name;
    value = e.target.value;
   console.log(name,value)

   setUserSignUp({ ...userSignUp, [name] : value});
  }


const PostData = async (e) => {
 e.preventDefault();
const {i_name, i_email, i_password, i_confirmPass} = userSignUp;
 const res = await fetch("http://localhost:5000/api/v1/users/signup",{
   method : "POST",
   headers : {
     "Content-Type" : "application/json"
   },
   body : JSON.stringify({
     i_name,i_email,i_password,i_confirmPass 
   })

 });

 const data = await res.json();

 if(data.status === 422 || !data) {
   window.alert("Invalid Registration");
   console.log("Unsuccessful");
 }
 else{
  window.alert("Valid Registration");
  console.log("Successful");

 }
}



    return (
        <div class='r'>
        
        <Form method = "POST" className='login col-lg-12'>
        
        <p className='welcome col-lg-12'>CREATE INTERVIEWEE CREDENTIALS</p>
        <p className='intro col-lg-12'>Create an account for interviewees!!</p>
        
        <Form.Group className='hi'>
        <Form.Group controlId="" className='email col-lg-12'>
          <Form.Label>NAME</Form.Label>
          <Form.Control type="text" placeholder="Enter full name" autoComplete="off" value={userSignUp.i_name} onChange={handleInput} 
          name = "name" id="name"/>
        </Form.Group>

        <Form.Group controlId="formBasicEmail" className='email col-lg-12'>
          <Form.Label>USERNAME</Form.Label>
          <Form.Control type="email" placeholder="Enter email" autoComplete="off" value={userSignUp.i_email} onChange={handleInput} 
          name = "email" id="email"/>
        </Form.Group>
      
        <Form.Group controlId="formBasicPassword" className='password col-lg-12'>
          <Form.Label>PASSWORD</Form.Label>
          <Form.Control type="password" placeholder="Enter password" autoComplete="off" value={userSignUp.i_password} onChange={handleInput} 
          name = "password" id="password"/>
        </Form.Group>
        <br /> <br />
        <Form.Group controlId="formBasicPassword" className='password col-lg-12'>
          <Form.Label>CONFIRM PASSWORD</Form.Label>
          <Form.Control type="password" placeholder="Confirm password" autoComplete="off" value={userSignUp.i_confirmPass} onChange={handleInput} 
          name = "confirmPass" id="confirmPass"/>
        </Form.Group>

       <br /> <br />  
        <Button className='b' type="submit" onClick={PostData}>
          SUBMIT
        </Button>
        </Form.Group>
      </Form>
      </div>
    )
}

export default CompanySignup
